package newone;

public class Facto {
	
	
	public static void fac()
	{
		int fact =1;
		int num=3;

		for(int i=1;i<=num;i++)
		{
			fact=fact*i;
			
		}
		System.out.println(fact);
			
	}

	public static void main(String[] args) {
		{
			
Facto.fac();			
		}
		// TODO Auto-generated method stub

	}

}

